INSERT INTO `wp_posts` VALUES("1506", "1", "2015-04-27 11:32:23", "2015-04-27 04:32:23", "Repository-hosted Themes are required to support display of comments on static Pages as well as on single blog Posts.  This static Page has comments, and these comments should be displayed.\nIf the Theme includes a custom option to prevent static Pages from displaying comments, such option must be disabled (i.e. so that static Pages display comments) by default.\nAlso, verify that this Page does not display taxonomy information (e.g. categories or tags) or time-stamp information (Page publish date/time).", "Page with comments", "", "inherit", "open", "open", "", "155-revision-v1", "", "", "2015-04-27 11:32:23", "2015-04-27 04:32:23", "", "155", "http://localhost/wordpress_dev/2015/04/27/155-revision-v1/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("1507", "1", "2015-04-27 11:32:23", "2015-04-27 04:32:23", "This static Page is set not to allow comments. Verify that the Page does not display a comment list, comment reply links, or comment reply form.\nAlso, verify that the Page does not display a \"comments are closed\" type message. Such messages are not suitable for static Pages, and should only be used on blog Posts.", "Page with comments disabled", "", "inherit", "open", "open", "", "156-revision-v1", "", "", "2015-04-27 11:32:23", "2015-04-27 04:32:23", "", "156", "http://localhost/wordpress_dev/2015/04/27/156-revision-v1/", "0", "revision", "", "0");
