INSERT INTO `wp_posts` VALUES("1566", "1", "2015-04-27 16:24:59", "2015-04-27 09:24:59", "Nested and mixed lists are an interesting beast. It\'s a corner case to make sure that\n<ul>\n	<li><span style=\"line-height:1.714285714;font-size:1rem;\">Lists within lists do not break the ordered list numbering order</span></li>\n	<li><span style=\"line-height:1.714285714;font-size:1rem;\">Your list styles go deep enough.</span></li>\n</ul>\n<h3>Ordered - Unordered - Ordered</h3>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item\n<ul>\n	<li><strong>unordered</strong></li>\n	<li><strong>unordered</strong>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n</li>\n</ul>\n</li>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n<h3>Ordered - Unordered - Unordered</h3>\n<ol>\n	<li>ordered item</li>\n	<li>ordered item\n<ul>\n	<li><strong>unordered</strong></li>\n	<li><strong>unordered</strong>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n</li>\n</ul>\n</li>\n	<li>ordered item</li>\n	<li>ordered item</li>\n</ol>\n<h3>Unordered - Ordered - Unordered</h3>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item\n<ol>\n	<li>ordered</li>\n	<li>ordered\n<ul>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n</li>\n</ol>\n</li>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>\n<h3>Unordered - Unordered - Ordered</h3>\n<ul>\n	<li>unordered item</li>\n	<li>unordered item\n<ul>\n	<li>unordered</li>\n	<li>unordered\n<ol>\n	<li><strong>ordered item</strong></li>\n	<li><strong>ordered item</strong></li>\n</ol>\n</li>\n</ul>\n</li>\n	<li>unordered item</li>\n	<li>unordered item</li>\n</ul>", "Edge Case: Nested And Mixed Lists", "", "inherit", "open", "open", "", "1000-revision-v1", "", "", "2015-04-27 16:24:59", "2015-04-27 09:24:59", "", "1000", "http://localhost/wordpress_dev/2015/04/27/1000-revision-v1/", "0", "revision", "", "0");
